css:
@-moz-document domain(www.youtube.com) {

.html5-play-progress, .ytp-play-progress {
   background: #FF80E5 !important; /* Old browsers */
   background: -moz-linear-gradient(left, #1b1b1b 0%, #FF80E5 100%) !important;
   background: -webkit-gradient(linear, left top, right top, color-stop(0%,#1b1b1b), color-stop(100%,#FF80E5)) !important;
   background: -webkit-linear-gradient(left, #1b1b1b 0%,#FF80E5 100%) !important;
   background: -o-linear-gradient(left, #1b1b1b 0%,#FF80E5 100%) !important;
   background: -ms-linear-gradient(left, #1b1b1b 0%,#FF80E5 100%) !important;
   background: linear-gradient(to right, #1b1b1b 0%,#FF80E5 100%) !important;
}
/*.html5-load-progress, .ytp-load-progress {
   background: #404040 !important;
}*/
.html5-scrubber-button, .ytp-scrubber-button {
   background: url("http://i.imgur.com/npBoqFp.gif") !important;
   width: 50px !important;
   height: 50px !important;
   border: none !important;
   margin-left: -15px !important;
   margin-top: -20px !important;
}
/* volume control */
.ytp-volume-slider-foreground:before {
   background: #FF80E5 !important; /* Old browsers */
   background: -moz-linear-gradient(left, #1b1b1b 0%, #FF80E5 100%) !important;
   background: -webkit-gradient(linear, left top, right top, color-stop(0%,#1b1b1b), color-stop(100%,#FF80E5)) !important;
   background: -webkit-linear-gradient(left, #1b1b1b 0%,#FF80E5 100%) !important;
   background: -o-linear-gradient(left, #1b1b1b 0%,#FF80E5 100%) !important;
   background: -ms-linear-gradient(left, #1b1b1b 0%,#FF80E5 100%) !important;
   background: linear-gradient(to right, #1b1b1b 0%,#FF80E5 100%) !important;
}
}
