css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("jsbin.com") {
  #panels {
    background:linear-gradient(#eee,#ccc)
  }
  #bin {
    z-index:unset
  }
  #runwithalerts {
    letter-spacing: -8px;
    color:transparent;
    height:35px;
    width:35px;
    position:fixed;
    bottom:5px;
    left:0px;
    z-index:9999;
    opacity:1;
    visibility:visible;
    transform:scale(0.75)
  }
  .options {
    position:fixed;
    bottom:12px;
    left:35px;
  }
  #runwithalerts:before {
    content:"";
    color:#000;
    display:inline-block;
    width:0;
    height:0;
    border:solid 10px transparent;
    border-left-color:#000;
    position:relative;
    top:3px;
    left:5px;
  }
  .gopro {
    display:none
  }
}
