css:
@-moz-document domain("twitch.tv") {
img[src="//cdn.betterttv.net/style/logos/logo_icon.png"], #sidebar_search, #nav_primary, #footer, .nav-divider, .nav_section:not(#nav_personal), .left-col-dropdown {
    display: none
}

#nav_personal {
    margin: 0;
    padding: 0;
    top: 0;
}

#large_nav .content, #small_nav .content {
    background-color: #222;
}

.username {
    background-color: rgba(0, 0, 0, 0) !important;
}

.game_filter.selected .title, .game_filter.selected .filter_icon,
.game_filter:hover .title, .game_filter:hover .filter_icon,
#large_messages:hover .title, #large_messages:hover .filter_icon {
    background: transparent !important;
}

 #broadcast-meta, .info, .js-directory.tse-content {
    background: #181818;
}

.ember-view.panel.masonry-brick {
    border: none !important;
    box-shadow: none !important;
    background: #222 !important;
}

.extra-menu {
    height: 136px !important;
}

#chat_line_list .line, .chat-messages .chat-line {
    background: #222 !important;
    border: 0 !important;
    box-shadow: none !important;
}

#chat_line_list .line .message, .chat-messages .chat-line .message {
    line-height: 175%;
}

.button.primary {
    background: rgb(50, 100, 200) !important;
}

.button.primary:hover {
    background: rgb(60, 110, 210) !important;
}
}
