css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
url-prefix(http://www.bbc.co.uk/persian)

{
	
	/* applies font to all generic HTML tags */
	*,
  html,
	body,
	span,
	div,
	td,
	br,
	input,
	font,
	button,
	select,
	option {
    font-family: IRANSansUltraLight,Noto Naskh Arabic,Iran,IRANSansUltraLight !important;
		font-size: 10pt !important;
	}
.story-body__h1 {
	font-family: IRANSans,Noto Naskh Arabic !important;
	font-size: 14pt !important;
}

.hard-news-unit__headline-link {
	font-family: IRANSans,Noto Naskh Arabic !important;
	font-size: 12pt !important;
}
	
.radio-promotion  *{
	font-size: 9pt !important;
}	
.navigation * {
	font-family: IRANSans,Noto Naskh Arabic !important;
	font-size: 9pt !important;
	
}
	/* specifies width of web search results column */

	td.j {
		width: 75% !important;
	}
}
