css:
@-moz-document domain("furaffinity.net") {

   .message-stream,  .cat, .alt1,.container-comment *,.messagecenter *,#JSMessage{
     font-size: small !important;
   }
  .replyto-message, .alt1{
    
    overflow: hidden;
  }
  center.messagecenter>b>span[title]{
    font-family: sans-serif;
    height: 6ex;
    overflow: hidden;
    line-height: 2ex;
   
  }
  
  .no_overflow>*{
    max-width: 70em;
    margin: auto;
    text-align: justify !important;
  }
  td[style="width:350px; height: 100%"]{
    display: none;
  }

}
