css:
@-moz-document url("http://www.pralek.cz/") {
@media all and (min-width: 1024px) and (max-width: 4000px) 
{body {
  background-color: LightGoldenRodYellow;
  font-family: Verdana,Arial,Helvetica,Geneva,SunSans-Regular,sans-serif;
}

h1 .red { color: MediumSpringGreen;}
h2      { color: ForestGreen; float:none; margin: 2em;}
h2 + p  { clear:both;}
a       { color: CornflowerBlue;}
ul li   {margin: 1em;padding: 0.2em;}
.socialIcons {text-align:center; margin: 3em;}}

/*--------------------------------------------------*/

@media all and (min-width: 768px) and (max-width: 1024px) 
{ h1 .red { color: MediumSpringGreen;}
h1{font-size:1.5em;}
h2      { color: orange; float:none; margin: 0.2em; font-size:1.2em;}
h2 + p  { clear:both;}
a       { color: steelblue;}
ul      {margin: 0;padding: 0.3em;}
ul li   {margin: 0;padding: 1em;display:block; max-width: 14em;float: left;}
.socialIcons {text-align:center; margin: 1em;}}
ul + li{ clear:both;}

@media all and (min-width: 480px) and (max-width: 767px) 
{ }

@media all and (max-width: 480px) 
{ h1 .red { color: MediumSpringGreen;}
h1{font-size:1.5em;}
h2      { color: orange; float:none; margin: 0.2em; font-size:1.2em;}
h2 + p  { clear:both;}
a       { color: steelblue;}
ul      {margin: 0;padding: 0.3em;}
ul li   {margin: 0;padding: 0.1em;}
.socialIcons {text-align:center; margin: 1em;}}


/* Portrait */
@media screen and (orientation:portrait) { }
/* Landscape */
@media screen and (orientation:landscape) { }

}
