css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("joyreactor.cc") {

/*Замена заднего фона
------------------------------------------------------------------------------*/
body {
 	  background: #aaa !important;
}
    
#container {
 	  background: #aaa !important;
    padding-top: 40px !important;
}  

#container:before,
#container:after {
 	background: none !important; 
}

/*Изменение стиля поста и формы 'новый пост'
------------------------------------------------------------------------------*/
#contentinner form {
    padding: 20px;
}

.postContainer {
    padding: 10px;
}
.postContainer,
#contentinner form {
    background: #fff !important;
    box-shadow: 0 1px 3px 0 rgba(0,0,0,0.12),0 1px 2px 0 rgba(0,0,0,0.24);    
    -webkit-box-shadow: 0 1px 3px 0 rgba(0,0,0,0.12),0 1px 2px 0 rgba(0,0,0,0.24);
    -moz-box-shadow: 0 1px 3px 0 rgba(0,0,0,0.12),0 1px 2px 0 rgba(0,0,0,0.24);
    margin-bottom: 20px;
}

#blogManagementLinks {
	background: none !important;  
}
  
.article,
.add_post_footer {
    margin-bottom: 0;  
}

/*Изменение цвета контейнера 
------------------------------------------------------------------------------*/
#pageinner {
 	background: #eee !important; 
}

.week_top,
.user.week_top .userposition,
.user.week_top a,
.user .weekrating {
    background-image: none !important; 
    background: none !important; 
}

/*AdBlock
------------------------------------------------------------------------------*/
#header {
    display: none !important;
}

BODY > #background > #container > SPAN > A[rel="nofollow"] {
    display: none !important;
}  
    
div#post_list > div {
    display: none !important;
}

div#post_list > div[id^="postContainer"] {
    display: block !important;
}

div#post_list > div[id^="comment"] {
    display: block !important;
}    
    
div[id^="ads_holder"] {
    display: none !important;
}
    
}
