css:
@-moz-document url-prefix("https://writer.bighugelabs.com/") {
body.home {
    background-image: url('https://apprecap.files.wordpress.com/2011/04/screen-shot-2011-04-08-at-10-49-50-pm.png') !important;
    background-size: cover !important;
}

#editorwrap {
    overflow: hidden !important;
}

#editorwrap #editor {
    overflow: auto;
}

#editorwrap, #editor, #mainform {
    border-color: transparent !important;
    background-color: transparent !important;
}

#scrollbar-cover {
    visibility: hidden;
}
}
