@-moz-document domain(trends24.in) {

.scrollable {
    position: static;
    overflow: auto;
    min-height: 22em
}
.scrollable .content {
    width: 100%;
    margin-top: 0;
    margin-bottom: 2em;
    overflow: hidden;
    position: static;
    -moz-transition: none;
    -webkit-transition: none;
    -ms-transition: nonet;
    transition: none;
}
.no-js .scrollable {
    overflow-x: none;
}

.trend-box {

    margin-bottom: 15px;

}

}
