css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp(".*://vimcolors\.com/.*(bg=dark.*|/dark$)") {
  body {
    background-color: black !important;
  }
  body, header h1 a {
    color: silver;
  }
  .colorscheme .colorscheme-details table {
    background-color: inherit;
  }
}
