css:
@-moz-document url-prefix("https://twitter.com") {
.AppContainer {
	max-width: none;
}

.Grid[data-component-term="user"] {
	display: flex;
}

.ProfileSidebar--withLeftAlignment {
	position: fixed;
	max-width: 25%;
}

body .wrapper {
	width: auto !important;
	display: flex;
}

.content-main {
	flex: 1;
	margin-right: 1em !important;
}

.stream-media-grid-items.media-item {
	display: flex;
	flex-wrap: wrap;
}

.with-media-forward .cards-media-container img {
	width: 100%;	
}

.tweet-row {
	max-width: none !important;
}
}
