css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("anekdotov.net") {
  BODY {background: #000;color: #85AB0A}
  td {padding: 10px;background: #171717;color: #85AB0A}
  .toptbl {display: none}
  .menuanekdot {color:#3C53D2}
  .calendar{display:none}
  A:link {color: #6A9811}
  .no_mobile {width:240px}
  .blutbl a {background:#00501A;color: #000}
  .pagenavi a, .pagenavi a:link, .pagenavimini a, .pagenavimini a:link {border:1px solid #4B4B4B;background-color:#000}
  .pagenavi span.current, .pagenavimini span.current {border: 1px solid #545454;color: #FFF;background-color: #245D19}
  table, td, th{background-color:#000}
  input {background: #333;border: none;color: #85AB0A}
  hr{display: none}
}
