css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://blog.livedoor.jp/geek/") {

  div#banner > table > tbody > tr[align="left"],
  div#banner > table td[height="90"] + td,
  div#container > div#links2,
  div#container > div#content > div:not(.blog)
  {display:none !important;}

  html{width: 100% !important;}
  
  body,
  div#container
  {max-width: 900px !important;}
    
  div#banner table,
  div#banner tbody,
  div#banner tr,
  div#banner td
  {width:100% !important;}
    
  div#content{
    padding: 0px !important;
    margin:  0px auto !important;
    left:0px !important;
    width:800px !important;
    position: static !important;
  }
  
}


@-moz-document url-prefix("http://blog.livedoor.jp/geek/") {
  
  
  div#links{display:none !important;}
  
  
}
