css:
/* i really want this to be global */ /* but it only applies when body has classes .mediawiki and .skin-vector */
body.mediawiki.skin-vector div#mw-panel, body.mediawiki.skin-vector div#p-personal ul {
  position: fixed;
  box-shadow: 0 0 20px #ddd;
  -moz-border-radius: 1em;
  border-radius: 1em;
  background: #f6f6f6;
}
body.mediawiki.skin-vector div#mw-panel {
  top: 7px;
  left: 7px;
  padding: 0;
  padding-top: 8px;
  z-index: 200;
}
body.mediawiki.skin-vector div#p-logo {
  position: static;
}
body.mediawiki.skin-vector div#p-personal ul {
  padding: 2px;
  padding-top: 0;
  padding-right: 9px;
  right: 7px;
  z-index: 202;
}
body.mediawiki.skin-vector li#wikEdLogoList {
  display:none;
}
body.mediawiki.skin-vector div#p-search {
  position: fixed;
  right: 7px;
  top: 25px;
  margin: 0;
  z-index: 201;
}
body.mediawiki.skin-vector div#simpleSearch {
  -moz-border-radius: 5px;
  border-radius: 5px;
  box-shadow: 0 0 10px #ddd;
}
body.mediawiki.skin-vector div.suggestions {
  position: fixed !important;
  right: 7px !important;
  top: 57px !important;
}
body.mediawiki.skin-vector div#left-navigation {
  margin-top: 10px;
}
body.mediawiki.skin-vector div#right-navigation {
  float: left;
  margin-left: 5px;
  margin-top: 10px;
}
body.mediawiki.skin-vector div#mw-page-base {
  height: 50px;
}
body.mediawiki.skin-vector div#footer {
  display:none;
}
