    E: ducky@zyr.io
*/

div#player.player {
    background-color: #0275D9;   
}

.navigationMenu>li .selected,
.menuButton.selected {
    background-color: #0275D9;
}

.iconPlayerShuffleActive {
    background-position: -34px -194px;
    background-color: #005FAE;
    border-radius: 30px;
}

.iconPlayerRepeatActive {
    background-position: -2px -162px;
    background-color: #005FAE;
    border-radius: 30px;
}

.playerNowPlayingMetadata > .secondaryMetadata > a {
    color: #DDDDDD;   
}

.playerNowPlayingMetadata > .secondaryMetadata > .label {
    border-color: #CCCCCC;
    color: #CCCCCC;
}

.playerVolumeIndicator {
    background-color: #005FAE !important;   
}

.iconSearchBox,
.iconSearchBox:hover,
.iconSearchSelectedBox,
.iconSearchSelectedBox:hover {
    background-position: -2px -634px;   
}

.metadataRow.grid.active > div > .primaryMetadata,
.mediaRow.active .primaryMetadata,
.mediaRow.active .secondaryMetadata {
    color: #0275D9;
}

.mediaRow.active .primaryMetadata {
    font-weight: 700;   
}

.highlightedLink,
a.highlightedLink,
a.highlightedLink:hover,
.sectionTitle a,
.sectionPageTitle a,
.alert .alertCancel,
.highlightedLinkInText a {
    color: #0275D9;   
}
}
