/*======================================
  moebuntu fc2 ja jp liquid compact 2015/05
  ======================================*/
@-moz-document domain("moebuntu.blog48.fc2.com") {
/*--- unwanteds ---*/
dummy#dummy.dummy {display: none !important;}

/*--- generic containers ---*/
body, div, h1, h2, h3, h4, h5, h6
 {box-sizing: border-box; -moz-box-sizing: border-box;}
body, div, p, span, h1, h2, h3, h4, h5, h6 {
 font-family: inherit !important;
 font-size: inherit !important; font-weight: inherit !important;
 line-height: 1.4em !important;
 }
#container {width: auto !important;}

/*--- header ---*/
#header {width: auto !important; margin: 0 !important;}
/*--- footer ---*/

/*--- main contents ---*/
#main, #sidemenu {position: relative !important;}
#main {width: 100% !important; margin: 0 0 0 -150pt !important;}
#main>* {width: auto !important; margin: 0 0 0 150pt !important;}
#main>* * {max-width: 100% !important;}
#sidemenu {width: 150pt !important; margin: 0 !important;}
#sidemenu * {max-width: 150pt !important;}

.entry_body {width: auto !important;}
}
