css:
/* i really want this to be global */
/* @todo: add transition transition: all 3s ease; */
/* Collapse description change */
.comment-change-wrapper > div.change-block > div * {
  display: none;
}
.comment-change-wrapper > div.change-block > div:before {
  content: "Description change ...";
  color: #900;
  font-weight: bold;
}
/* Hover */
.comment-change-wrapper:hover > div.change-block > div * {
  display: inline;
}
.comment-change-wrapper:hover > div.change-block > div:before {
  content: "";
}
