css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("smart-lab.ru") {

/*SL-style-original v.0.1*/
#chat_counter span, /*секретный чат*/
#header .profile ul li strong /*рейтинг*/   
{
    background: none !important;
    padding: 0 !important;
}

.topic .feed:nth-child(2n+1) /*зебра в ленте*/
{
    background-color: #fff;
}

.topic h3 a.user /*имя в ленте*/
{
    font-weight: normal !important;
}

.feed .inside, /*текст в ленте*/
.topic h3 a, /*ссылки в ленте*/
.topic h3 span.user, /*дата и пайпы в ленте*/
.feed .rating .c /*количество комментов*/
{
    color: black !important;
}

.topic .feed /*строки в ленте*/
{
    border-bottom: none !important;
    font-size: 10px !important;
}

.feedtop .rating .c, /*количество комментов на главной*/
.feed:hover a, /*ссылки в ленте при наведении*/
.feed:hover .user /*строки в ленте при наведении*/
{
    color: #fff !important;
}

.feed a:last-child, /*ссылка на пост*/
.feed .rating .c /*количество комментов*/
{
    color: #F2650F !important;
    font-weight: bold !important;
}

.feed .rating .r /*плюсы*/
{
    color: #007F06 !important;
    font-weight: bold !important;
}

.topic h3 a.status /*статусный юзверь*/
{
    color: #ab3c05 !important;
}

.topic h3 a.paid_blog /*рекламный юзверь*/
{
    color: #007F06 !important;
}

}
