css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.petforums.co.uk") {
  /* main background */
  #uix_wrapper {
    background-color: #7f6a41 !important;
  }
  /* profile dropdown */
  .primaryContent, .secondaryContent, .sectionFooter {
    background-color: #f1f9f6 !important;
    border-color: #7f6a41 !important;
  }
  .blockLinksList a, .blockLinksList label {
    background-color: #f1f9f6;
    border-color: #7f6a41 !important;
  }
  .sectionMain {
    background-color: #b4d9c9 !important;
    border-color: #7f6a41 !important;
  }
  .nodeList {
    background-color: #f1f9f6 !important;
    border-color: #7f6a41 !important;
  }
  /* thread */
  .discussionListItem {
    background-color: #f1f9f6 !important;
    border-color: #7f6a41 !important;
  }
  /* sticky thread */
  .discussionListItem.sticky {
    background-color: #e1e9e6 !important;
    border-color: #7f6a41 !important;
  }
  /* message background */
  .message {
    background-color: #f1f9f6 !important;
    border-color: #7f6a41 !important;
  }
  /* title bar */
  .mainContainer_noSidebar {
    background-color: #b4d9c9 !important;
    border-color: #7f6a41 !important;
  }
  /* breadcrumbs */
  .breadcrumb {
    background-color: #f1f9f6 !important;
    border-color: #7f6a41 !important;
  }
  /* footer */
  footer, .footer .pageContent {
    background-color: #b4d9c9 !important;
    border-color: #7f6a41 !important;
  }
  /* footer columns */
  #uix_footer_columns .pageContent {
    background-color: #f1f9f6 !important;
    border-color: #7f6a41 !important;
  }
  /* redactor */
  .redactor_toolbar, .redactor_toolbar li {
    background-color: #f1f9f6;
    border-color: #7f6a41 !important;
  }
  /* text controls */
  .textCtrl {
    background-color: #f1f9f6 !important;
    border-color: #7f6a41 !important;
  }
}
