css:
@-moz-document domain("reactor.cc"), domain("joyreactor.cc") {
.topbar_wr,.topbar_wr a{line-height: 22px !important;}
.topbar_wr
{
	height: 22px !important;
}
.sswither,.lang_select a img  {height: 16px !important;}
.sswither {margin: 3px !important;}
.sswither a span {height: 8px !important;   top: 2px !important;}

.topbar_right .login_wr .login .icon_mail
{
    height: 12px !important;
    width: 16px !important;
	margin: 4px !important;
}
#container
{
    padding-top: 22px !important;
    background-position: 50% -22px;
}
}

@-moz-document regexp("(.*\\.reactor.*)") {
#container
{
    padding-top: 22px !important;
    background-position: 50% 22px;
}
}
@-moz-document domain("old.reactor.cc") {
#container
{
    padding-top: 0px !important;
}
}
