css:
@-moz-document domain(sonicrainboom.com.br) {
.thead {
 background-color: #F0B200
}
.tcat {
  background: #CC9800;
  color: #fff;
  font-size: 12px;
}
img.logo_image {
 -webkit-filter: hue-rotate(35deg) saturate(1.3);
}
#header_wrapper {
  height: 281px;
  width: 1012px;
  background-color: #F0B200;
  margin-left: auto;
  margin-right: auto;
  padding-top: 5px;
  position: relative;
}
a:visited {
  color: #9C7400;
  text-decoration: none;
}
a:link {
  color: #9C7400;
  text-decoration: none;
}
a:hover {
  color: #F0B200;
}
.tfoot {
  background-color: #CC9800;
  color: #ffffff;
}
div.footer {
  padding-top: 5px;
  background-color: #F0B200;
  width: 980px;
}
.menu ul a:visited {
  color: #FFD764;
  text-decoration: none;
}
.menu ul a:link {
  color: #FFD764;
  text-decoration: none;
  background-color: #343746;
  display: block;
  float: left;
  padding-bottom: 8px;
  padding-top: 8px;
  padding-left: 16px;
  padding-right: 16px;
}
.menu ul a:hover {
  background-color: #F0B200
}
div.board_message_dash a:visited {
  color: #F0B200
}
div.board_message_spike a:visited {
  color: #F0B200
}
div.board_message_pinkie a:visited {
  color: #F0B200
}
div.board_message_dash a:link {
  color: #F0B200
}
div.board_message_spike a:link {
  color: #F0B200
}
div.board_message_pinkie a:link {
  color: #F0B200
}
div.board_message_dash a:hover {
  color: #FFD250
}
div.board_message_spike a:hover {
  color: #FFD250
}
div.board_message_pinkie a:hover {
  color: #FFD250
}
.panel a:link{
  color: #F0B200
}
.panel a:visited{
  color: #F0B200
}
.panel a:hover{
  color: #FFD353
}
