css:
/* Mini-style that enhance some little things on FA... */

@-moz-document domain("www.furaffinity.net"), domain("sfw.furaffinity.net") {
  /* Font Swap */
    table, tr, td, th, body {
        font-family: "Trebuchet", Helvetica, sans-serif;
        -webkit-font-smoothing: antialiased !important;
        text-shadow: 0px 0px 3px rgba(0,0,0,0.70), 1px 1px 3px #000000;
    }
    ul.dropdown, ul.dropdown li, ul.dropdown ul {
        font-family: "Trebuchet", Helvetica, sans-serif;
        -webkit-font-smoothing: antialiased !important;
    }
  /* Artist Informations Tweak */
    td.alt1.user-info > span {
        color: #0480A4;
        font-weight: bold !important;
    }
    td.user-contacts th {
        color:#0480A4;
        font-weight: normal !important;
    }
  /* Small things */
    center.flow.userpage-submissions.twolines {
        background-color: rgba(0,0,0,0.25) !important;
        border: 3px double rgba(0,0,0,0.40) !important;
    }
    center.flow.userpage-favorites.twolines {
        background-color: rgba(0,0,0,0.25) !important;
        border: 3px double rgba(0,0,0,0.40) !important;
    }
    .lead {
       height: 12px;
    }
/*-----------------------------------------------------------------------------------------------------------------------------------------------------------------/*
  /* Navigation Bar */
    .block-menu-top {
        z-index: 42;
        top:0px !important;
        position: fixed;
    }
    ul.dropdown > li {
        line-height:0.7em !important;
    }
    ul.dropdown li hr {
        border: 0;
        height: 2px;
        background-image: linear-gradient(to left, rgba(255,255,255,0), rgba(255,255,255,1.00), rgba(255,255,255,0));
    }
/*-----------------------------------------------------------------------------------------------------------------------------------------------------------------/*
  /* Custom Quotes */
    .bbcode_quote {
        background-color: rgba(0,0,0,0.25) !important;
        border: 3px double rgba(0,0,0,0.40) !important;
    }
    .bbcode_quote A:link {
        color: #0480A4 !important;
    }
    .bbcode_quote A:visited {
        color: #0480A4 !important;
    }
    .bbcode_quote A:hover {
        color: #1398A9 !important;
    }
  /* Custom Horizontal Lines (HR) */
    .bbcode_hr {
        border: 0;
        height: 2px;
        background-image: linear-gradient(to left, rgba(255,255,255,0), rgba(255,255,255,0.90), rgba(255,255,255,0)); }
    }
