css:
@-moz-document domain("vnexpress.net") {

  .block_col_480 {
    font-family: Verdana;
    width: 100% !important;
    float: left;
    background: white !important;
  }
  .block_col_480 div {
      font-family: Verdana !important;
      font-size: 14px;
  }

  .box_category img, .block_100.hide_tablet_768, #box_khuyenmai {
      width: 100%;
  }

  .line_col, .line_col_midnews_top {
      background: none;
  }

  .line_col_midnews_top > .box_hot_news {
      width: 81%;
  }

  .width_common.line_col > #col_1, .width_common.line_col > #col_2 {
      width: 48.47%;
  }

  body {
      background-image: none !important;
  }

  img[src$=".gif"], img[src*="eclick"] {
      display: none;
  }

  .box_category{
   margin-bottom: 0px;
  }

}
