css:
@-moz-document domain("vietnamnet.vn") {
#MidLeftWraper {
    width: 100%;
}

#MidRightWraper,
#BodyRightWraper,
#TopWraper > div,
#TopWraper > ul, 
.fmsidWidgetTaskbar,
.vmc_ads_viewport {
    display: none;
}

.BodyLayout220 {
    width: 33%;
}

.BodyLayout460 {
    width: 66%;
}

.BodyLayout690,.topCategoryNewsLastest {
    width: 100%;
}

.topNewsLastest > div, iframe {
    width: 100%;
}

.topNewsLastest,.topHotAndLastestTab,#BodyLeftWraper,#BodyCenterWraper {
    width: 49%;
}

.topNewsLastestFirst img[src*=vietnamnet] {
    height: auto;
    width: 100%;
}

.topNewsLastestListItem {
    margin-right: 27px;
}

.topNewsLastestListItem:last-child {
    margin-right: 0px;
}

div[style^=" "] {
    width: 100%;
}

table.image.center {
    max-width: 100%;
    width: 100%;
}

table.image.center img {
    max-width: 100%;
    width: 100%;
}


}
