@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(chrome://sage/content/feedsummary.html) {
body, html {background-color:#000000 !important;}
body {
	color:#fff !important;
	font-size:11px !important;}
img {border:0px !important;}

#rss-header {
	border:0px !important;
	background-color:#000 !important;}
a {color:#bb3333 !important;}
.item {border:0px !important;}
.item-ordinal {color:#c00 !important;}
.item-title {
	background-color: #400000 !important;
	-moz-border-radius:3px;}
.item-title a {color:#f00 !important;}
.item-title a:visited {
	font-style:italic !important;
	color:#c00 !important;}
.item-desc { padding:5px 5px 0px 2em !important;}
}
