css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("acm.csie.org") {
  .layoutwrapper {
    background: transparent;
  }
  [bgcolor] {
    background: transparent !important;
  }

  body, .note {
    color: lightgrey;
  }
  [color="#336699"] {
    color: cyan !important;
  }
  a, .layoutmain a:hover, a:visited {
    color: green;
    text-decoration: none;
  }
  
  .layoutcenter, .layoutwrapper {
    text-align: center;
    display: block;
    width: 99%;
    margin-top: 0;
  }
  .layoutmain, [width="790"], [width="750"], [width="700"], [width="600"], [width="500"], [width="40%"] {
    width: 100%;
  }
  [width="40%"] .regform tr {
    display: inline-block;
  }
  [width="40%"] .regform td {
    display: inline-block;
    width:5em;
    text-align: left;
  }
  
  table.regform, td.statusform, td.regform {
    border: none;
  }
  .SelectedRow {
    background: transparent;
  }
  form td.regform:first-child {
    text-align: right;
  }
  td.regform {
    font-size: initial;
  }
}
