css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("chat.plan.ru") {

  #container_chat { 
    height: auto;
}

   #menu_container_inside, #inner_header {
    float: right;
    margin-right: 0px;
    width: 98%;
  }
  
  #container_chat {
    float: right;
    margin-right: 0px;
    width: 98%;
    height: 100%;
  }


  #header {
    height: 5%;
    margin: 0 auto;
    padding: 5px 0;
    position: relative;
    top: 0;
    width: 100%;
  }
  
  #wrap_topic {
    height: 0%
  }

  #room_topic {
    height: 0%
  }
  
  #main_emoticon {
    padding: 0;
}

  
  #show_chat li {
    position: relative;
  }

  .ch_logs {
    border-bottom: medium none;
  }
  
  .log1 {
    background: none repeat scroll 0 0 rgba(0, 0, 0, 0);
  }
  
  .log2 {
    background: none repeat scroll 0 0 #282828;
  }
  .public {
    font-size: 13px;
  }
  .ch_logs {
    padding: 3px 1%;
    word-wrap: break-word;
  }
  
  
  #main_input {
    height: 24px;
    margin: 4px 0;
    width: 100%;
  }
}
