css:
@-moz-document domain("theartofinsulting.com") {
body {
    background: black;
    color: white;
}

.wrapper {
    position: relative;
    margin-top: 150px;
}

#insult *, #footer * {
    color: white;
}

#insult, #again, #footer, #main_text {
    float: left;
    width: 30%;
    margin-left: 35%;
    left: inherit;
    position: relative;
    top: inherit;
}

#sir {
    position: absolute;
    top: 0;
    right: 0;
    float: none;
    left: inherit;
}

#sir img {
    -webkit-filter: invert(100%); 
    -moz-filter: invert(100%);
    opacity: 0.75;
    max-width: 35%;
}


/*
	Hiding ads, "next" button and some texts
*/

#banner_ad {
    display: none;
}

#again a, #again p {
    display: none;
}

/*
	Re-showing "next" button and text above "next" button
*/

#again a.button, #again p:nth-child(1) {
    display: block;
}
}
