css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bing.com") {

/*布局拉宽*/
#b_content > #b_results{width:100% !important;float:none!important;}

/*外框美化*/
#b_content{border: 1px solid #ccc;padding:0!important;margin:35px 35px!important;}

/*也试试*/
#b_content > div.b_underSearchbox{background-color: #F5F7FA!important;padding:8px 10px!important;margin:0px!important;border-bottom:solid 1px #ccc;}

/*提示*/
#b_content > #b_pole{background-color: #EAEBEC!important;padding:8px 10px!important;margin:0px!important;border-bottom:solid 1px #ccc;}

/*奇数行美化*/
#b_content > #b_results > li:nth-child(even) {background-color: #EAEBEC!important;padding:8px 10px!important;margin:0px!important;border-bottom:solid 1px #ccc;}

/*偶数行美化*/
#b_content > #b_results > li:nth-child(odd) {background-color: #F5F7FA!important;padding:8px 10px!important;margin:0px!important;border-bottom:solid 1px #ccc;}

/*分页行美化*/
#b_content > #b_results > li:last-child {padding:20px 10px!important;border-bottom:none !important;}

/*搜索结果悬停美化*/
#b_content > #b_results > li:not(:nth-last-child(-n+2)):hover {background-color: #B5E0F5!important;}

}
