css:
@-moz-document url-prefix("https://play.google.com/music/listen") {

/* Remve large margin at the top of albums with artist artwork */

.material-detail-view .has-hero-image {
    margin-top: calc(5vw - 10px - 20px);
}

/* De-round artist portraits */

.entity-card.material-card .image, .entity-card.material-card .letter-art {
    border-radius: 0;
    border-top-left-radius: 0;
    border-top-right-radius: 0;
    border-bottom-right-radius: 0;
    border-bottom-left-radius: 0;
}

.entity-card.material-card .image-wrapper {
    position: relative;
    border-radius: 0;
    border-top-left-radius: 0;
    border-top-right-radius: 0;
    border-bottom-right-radius: 0;
    border-bottom-left-radius: 0;
}

/* Widens album tracklist */

core-header-panel #music-content.material .g-content {
    margin: 0;
}

/* Maintains visibility of song duration */

#material-player-right-wrapper .player-top-right-items .time-label {
    opacity: 100;
    transition: opacity .100;
}

#player.material #time_container_current, #player.material #time_container_duration {
    opacity: 100;
}

/* Widen settings cards */

.settings-card {
    width: 88%;
    max-width: 88%;
}
}
