css:
@-moz-document regexp("(.+)?(play\\.rust-lang)(.+)?") {
/* Christopher Welborn 12-6-14 */
div#editor.ace_editor.ace-github, #editor, #result {
    font-size: 0.9em !important;
    font-family: "DejaVu Sans Mono", "Source Code Pro", monospace !important;
}

button.primary {
      background-color: #2859C2 !important;
}

input[type=radio]:checked + label {
  background: rgba(11, 111, 244, 0.5) !important;
}

input[type=radio]:checked + label:hover, input[type=radio]:checked:focus + label, input[type=radio]:checked:active + label {
  background: rgba(58, 143, 203, 0.7) !important;
}

.radios label {
  background: rgba(41, 107, 195, 0.1) !important;
  border: 1px solid rgba(0, 31, 111, 0.4) !important;
}
}
