css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("news.ycombinator.com") {
    body {
    background-color: #282828 !important;
    color: #F4E8BA !important;
  }
  
  a {
    color: #FABD2F !important;
  }
  
  a:visited {
    color: #B57614 !important;
  }
  
  body > center > table > tbody > tr:first-child > td {
    background-color: #1D2021;
  }
  
  #hnmain {
    background-color: #282828 !important; 
    margin-left: auto;
		margin-right: auto;
  }
  
  .comment, .comment font {
    color: #F4E8BA !important;
  }
  
  #hnmain > tbody:nth-child(1) > tr:nth-child(4) > td:nth-child(1) > table:nth-child(2) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) {
    background-color: #79740E !important;;
  }
  
  input, textarea,select {
    background-color: #32302F;
    border: 1px #1D2021;
    color: #F4E8BA;
  }
}
