css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("anamafia.web.fc2.com") {
  
  /* AD */
  div[id*="fc2"]{display: none !important;}
  
  
  body{
background:#151515;
font-family : "Meiryo" !important;
  line-height:1.6em !important;
  letter-spacing:0.8pt !important;
    width:50% !important;
    margin: 0 auto;
    top:-350px !important;
    margin-bottom: -350px !important;
position: relative !important;
  }
  
  font,*[style*="font"]{font-family : "Meiryo" !important;}
  
  
  h3{
    margin-top: 3em;
    padding-left: 1em;
    margin-left:0 !important;
  border-left: solid 8px white;
  }
  
  h4{text-align: center;}
  p{margin: 0 auto !important;
  color: white !important;}
  }

/* リンクページ */
@-moz-document url("http://anamafia.web.fc2.com/LINKS.htm"){
  table{width: 620px !important;
  margin:0 auto !important;
  border-collapse:collapse !important;}
  table p{border: none !important;
  padding:0 !important;
}
  table td{border: 4.50pt double #000000 !important;}
}

/* ブログ */
@-moz-document url-prefix(http://blog.livedoor.jp/mafia720){

  #header2{display: none !important;}
  
  .checkers,.reader,.powered,.plugin-news{display: none !important;}
  
}
