@-moz-document domain("watchwrestling.ch") {
 body {background:#000000; color: #ffffff;} 
  #main {
    background: none;
    border-top: 2px solid #FFF;
    padding: 30px 0px;
}
  table, tr, td {
    border: 0;
  }
a {
    color: #ffffff;
    text-decoration: none;
    outline: 0px none;
    transition: none;
}
body.plugin, #widget {display:none;}
.widget-title {
    display: block;
    border-bottom: 1px solid #fff;
    line-height: 24px;
    font-size: 15px;
    margin: 0px;
    font-family: "Open Sans",sans-serif;
    padding-bottom: 6px;
  color:#fff
}
  .item .entry-title a {
    color: #fff;
}
