css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("duckduckgo.com") {

    .content--home,
    .header-wrap,
    .content-wrap
     {
         background-color:#000 !important;
         background-attachment:fixed;
         background-position:right;
         background-repeat:no-repeat;
         background-size:100% 60%;
     }
    .result
    {
        background: #000;
        border: 1px #23B6DB solid;
    }
    .result.highlight
    {
        background: #333;
    }
    .result__check
    {
        color: #000;
    }
    .result__a,
    .zci h1 a,
    .zcm__link--sub:hover,
    .zcm__link--sub.is-highlighted
    {
        color: #FFF;
    }
    .site-wrapper,
    .page-index,
    #zci-about,
    #zero_click_wrapper div,
    .acp-wrap
     {
         background: #000000;
     }
    .has-active-zci .header:after,
    .has-active-zci .header-wrap:before,
    .has-active-zci .header-wrap:after,
    .nav-menu__list,
    .zci__metabar-wrap,
    #zci-videos,
    #header,
    .header
     {
         background-color: #000000;
     }
    .search--home,
    .search--adv
     {
         background: #000000;
         border: 1px #23B6DB solid;
     }
    .btn
    {
        color: #23B6DB;
        background: #000000;
        border: 1px #23B6DB solid;
    }
    .btn:hover
    {
        color: #000000;
        background: #23B6DB;
        border: 1px #23B6DB solid;
    }
    .search__autocomplete,
    .acp,
    #did_you_mean
    {
        color: #FFFFFF;
        background: #000000;
        border: 1px #23B6DB solid;
    }
    .result__snippet b
    {
        color: #23B6DB;
    }
    a,
    strong,
    .result__snippet,
    #search_form_input_homepage,
    .search__input--adv,
    .info,
    .zci__content--info,
    .zci__body,
    .nav-menu__heading span,
    .zci__metabar__primary-text,
    .tile__content,
    .tile__title,
    .results--message__safe-search
     {
         color: #FFFFFF;
     }
    .tile--img__details,
    .tile--img__dimensions
     {
         opacity: 0.7 !important;
     }
    .tag-home__nav__wrap:hover,
    .badge-link
     {
         background-color: #222;
     }
}
