css:
@-moz-document domain("onlinebanking.usbank.com") {
#firstLevelNav > li.firstLevelNavLi {
    padding: 0 !important;
}
#firstLevelNav > li.firstLevelNavLi > .usbMenu-span > .usbMenu-a,
#firstLevelNav > li.firstLevelNavLi > .usbMenu-a {
    padding: 13px 20px;
}
.usbMenu-navigationFirstLevel {
    cursor: initial;
}
.usbMenu-secondLevelNav > li {
    cursor: pointer;
    padding: 0 !important;
}
.usbMenu-secondLevelNav > li:hover {
    background-color: #6698c3;
    background-color: rgba(255, 255, 255, 0.2);
}
.usbMenu-secondLevelNav > li > .usbMenu-a {
    padding: 9px 20px;
}
#accountSelectorLi {
    padding-right: 8px !important;
}
}
