css:
@-moz-document url-prefix("http://www.xiami.com/radio/play/") {

.radio_player{
  position:fixed;
    top:0;
    left:0;
    right:0;
    z-index:999999;
}
.radio_player:before{
    content:'';
    
     background:#fff -webkit-gradient(linear,left top,left bottom,from(rgba(0, 0, 0, 0.2)),to(transparent));
    
  position:fixed;
    top:0;
    left:0;
    right:0;
    bottom:0;
}
#feedback,.radio_player_hq{
  display:none !important;
}
#p-nowrap{
 z-index:9999999;
}

.radio_player_inner{
box-shadow: 5px 5px 20px #333; box-sizing: border-box; height: 180px;
    margin: 0 auto;
    overflow: hidden; position: relative; width: 700px;
}

body{
overflow:hidden;
   
}
}
