css:
@-moz-document domain("github.com") {
body {
   font-size: calc(13px + 0.05vw);
}

h3 {
   font-size: calc(12px + 0.05vw) !important; 
}

.container {
   width: 98%;
   min-width: 965px;
   margin-left: 1%;
   margin-right: 1%;
}

.site-search.repo-scope.js-site-search {
   margin-left: 2%;
}

.header-nav.left {
   margin-left: 1%;
}

.repository-with-sidebar.with-full-navigation .repository-content {
    max-width: calc(80% - 10px);
   min-width: 700px;
}

.file-wrap {
   margin-bottom: 20px;
}

#js-repo-pjax-container {
   width: calc(98% - 38px);
	
}

.repository-with-sidebar.with-full-navigation .repository-sidebar {
   width: calc(20% - 10px);
   min-width: 160px;
}
.sunken-menu.repo-nav.js-repo-nav.js-sidenav-container-pjax.js-octicon-loaders {
   margin-bottom: 20px;
}

.repository-with-sidebar .repository-sidebar .sidebar-button {
   width: 75%;
   min-width: 150px;
}
}
