css:
@-moz-document domain("play.baidu.com"), domain("music.baidu.com") ,domain("y.baidu.com") {
/**
iframe,.column4,#pauseAd
    **/
 .hot-search,.song-lyric+[id^="show"],#download-clients,
.music-body>.ad-banner ,.sidebar>[id^="show"],.sidebar>#adResultRight,[id*="adResult"],#iframeID_100,#toptip,.gold-vip-tip,.vip-icon,#right-ads,#pauseAd,#m-client-product,.download-button,.hq-switch .icon,.share.wg-button,.audio-effect,#btnSimilar.similar-song,#playerD2m.d2m
,.qr-section,.qr-section+.cover-list  ,#adMainTopLeft,[monkey="NI_girl_musician"],[monkey="NI_events"]
,[monkey="mbox_entry"],.music-head-nav .search-btns,[monkey="NI_king"],#adBottom,[monkey="NI_lebo"],.music-foot [class*="follow"]
,.async-module-sidebar,.cbg-Ads,.search-wrap>.lk,#user-info-wrapper,.bd-feedback,.bd-feedback+.line,.vip-intro,.vip-intro+.line
,.nav-banner>li:nth-child(n+3) ,[monkey="NI_hot_musician"],[id^="BAIDU_DUP"],[id^="adMain"]

,[monkey="NI_girl_live"],.nav-banner.nav-ads,.mod-hot-message
  {
    display: none !important;
}
.mod-focus .mui-slider .mui-slider-scroll-wrap {
    width: 150%;
}  
.mod-focus .mui-slider .control-right,.mod-focus .mui-slider-controller {
    margin-right: -280px;
}
.hd  .title {
    width: inherit;
    height: 30px;
    text-indent: 0;
    top: 8px;
    background:none;
} 
.mod .hd .title, .mod .bd .title {
    font-size: 15px;
}  
.music-head .lg {
    padding: 0px 16px 20px 0px;
    margin-top: -10px;
}   
.main-wrapper>.mb-layout-bd.column2 {
    right: 300px;
} 
    
.main-wrapper>#lrcCol.column3 {
    right: 0px;
    width: 300px;
}   
.main-wrapper>.mb-layout-bd.column4 {
    width: 0px;
}  
.playlist-length-cloud {
    bottom: 0px;
}
.leftbar-outer {
    bottom: 98px;
}   
.album-wrapper  .album img {
    box-shadow: 1px 1px 2px rgb(17, 17, 17), -2px -2px 3px rgb(167, 167, 167), 2px -2px 3px rgb(167, 167, 167), -2px 2px 3px rgb(167, 167, 167);
    border-radius: 50%;
    border: 3px solid rgba(255, 255, 255, 0.4);
    animation: kp3 6.6s infinite  linear ;
    -moz-animation: kp3 6.6s infinite  linear ;
    -webkit-animation: kp3 6.6s infinite  linear ;
    -o-animation: kp3 6.6s infinite  linear  ;
}  
.album-wrapper>.album:before {
    content:" ";
    display:block;
    left: calc(50% - 22.5px);
    top: 72px;
    height:45px;
    width:45px;
    position: absolute;
    z-index: 20;
    border-radius: 50%;
    background-color:rgba(255, 255, 255, 0.5)  ;
    
    box-shadow: 1px 1px 1px rgba(0,0,0,.1) , -1px -1px 1px rgba(0,0,0,.1);
}  
.album-wrapper>.album:after {
    content:" ";
    display:block;
    left: calc(50% - 10px);
    top: 85px;
    height:20px;
    width:20px;
    position: absolute;
    z-index: 21;
    border-radius: 50%;
    background-color:rgba(255, 255, 255, 1)  ; 
    box-shadow: 1px 1px 2px rgb(167, 167, 167) , 1px -1px 2px rgb(167, 167, 167) , -1px 1px 2px rgb(17, 17, 17) inset;
}  
    

@keyframes kp3
{
0%   {transform:rotateZ(0deg);}
100% {transform:rotateZ(359deg);}
}

@-moz-keyframes kp3
{
0%   {-moz-transform:rotateZ(0deg);}
100% {-moz-transform:rotateZ(359deg);}
}

@-webkit-keyframes kp3
{
0%   {-webkit-transform:rotateZ(0deg);}
100% {-webkit-transform:rotateZ(359deg);}
}

@-o-keyframes kp3
{
0%   {-o-transform:rotateZ(0deg);}
100% {-o-transform:rotateZ(359deg);}
}
   
.artist-cover.cover-img img:before {
    content:" ";
    display:block;
    width: 20px;
    height:20px;
    background: red;
    position: absolute;
    z-index: 100;
}
    
    
}
