@-moz-document domain("overclockers.ge") {
html[lang="en-US"] > body {
  /*background-color: #1B1B1B; */
  font-size: 12px;
  line-height: 14px;
  color: #1B1B1B;
  background-image: url("http://images6.alphacoders.com/456/456498.jpg")
}
/*mtavari kontentis sigane*/
.ipsLayout_container {
