}
/*zeda headers foni*/
#ipsLayout_header header {
  /*background-color: #1B1B1B;*/
  background-image: url("http://www.picz.ge/img/s2/1505/14/a/a0eabffb1968.jpg")
}
/*esaa axali logo*/
#elLogo img {
  display: block;
  -moz-box-sizing: border-box;
  box-sizing: border-box;
  background: url(http://www.picz.ge/img/s1/1505/15/f/f2ab3c5bcdd4.jpg) no-repeat;
  width: 180px; /* Width of new image */
  height: 100px; /* Height of new image */
