}
/*FORUMS warweris dabla tagebis moshla*/
.ipsTags {
  margin-top: 7px;
  display: none;
}
.ipsApp .ipsSpacer_bottom, .ipsApp .ipsSpacer_both {
  margin-bottom: 0px;
}
/*moxra forums warweraze*/
.ipsType_sectionTitle {
  border-radius: 0px 0px 0 0;
}
/*monacemebis xazebis raodenoba*/
ul[class="cAuthorPane_info ipsList_reset"] > li:nth-of-type(n + 7)  {
  display: none !important;
}
/*users postebis shavi wertilebis tavze warweris gaqrona*/
html[dir="ltr"] .ipsPip {
    display: none;
}
/*kutxeebi marjvena velis tavshi lurjshi momrgvalebuli kutxeebis gaqrobac*/
.ipsWidget.ipsWidget_vertical .ipsWidget_title {
    padding: 11px;
    background: #333333;
    border-radius: 0px 0px 0px 0px;
}
/* sul marjvniv padding gare zoltan*/
html[dir="ltr"] #ipsLayout_sidebar.ipsLayout_sidebarright, html[dir="rtl"] #ipsLayout_sidebar.ipsLayout_sidebarleft {
    padding-right: 0px;
    padding-top: 43px;
}
/*ganyofilebebis satauris zolis feri*/
.ipsType_sectionTitle {
    background: #333333;
    color: #ffffff;
    border-radius: 0px 0px 0px 0px;
}
/*mtavari satauris ganyofilebebis feri*/
.ipsNavBar_primary {
    position: relative;
    padding: 0px 0px 40px 0px;
    z-index: 2000;
    background: #333333;
    border-radius: 0px 0px 0px 0px;
}
/* mtavari navigaciis velebshi gilakebs shoris adgili*/
html[dir="ltr"] .ipsNavBar_primary > ul > li > a {
    margin-right: 0px;
}
/*shua forumis padding marjvena kidestan*/
#ipsLayout_mainArea {
    padding: 0px 0px;
}
/*forum warweris tavze*/
.ipsBreadcrumb.ipsBreadcrumb_top {
    border-bottom: 0px solid rgba(0,0,0,0.075);
    margin: 0px 20px 0px 20px;
    padding: 12px 0;
}
/*tebebshi shida satauris qveda velis feri*/
.ipsButtonBar {
    line-height: 1;
    background: #333333;
}
/*komentarebs shoris manzili*/
.ipsComment {
    margin-bottom: 0px;
    padding: 0;
}
/*komentarebis irgvliv sazgvari*/
.ipsPad, .ipsApp ul.ipsPad, .ipsApp ol.ipsPad {
    padding: 0px;
}
/*user fotoebis zomis gazrda temebs garet*/
.ipsUserPhoto_tiny img, img.ipsUserPhoto_tiny, .ipsUserPhoto_tiny:after {
    width: 39px;
    height: 39px;
}
/*user fotoebis zomis shemcireba temebs shignit*/
.ipsUserPhoto_large img, img.ipsUserPhoto_large, .ipsUserPhoto_large:after {
    width: 75px;
    height: 75px;
}
/*komentaris marcxniv adgilis gaqroba*/
html[dir="ltr"] .cPost .ipsComment_content {
    padding-right: 0;
    padding-left: 0px;
}
/*temebshi userebis velis sigane*/
.ipsColumn_medium {
    width: 180px;
}
/*userfotoebis garshemo adgilebis gaqroba*/
