css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("2ch.net"),domain("2ch.sc") {
body div.js--ad--top + div{
  margin-top: 0em !important;
}
h1{
  color: black !important;
  font-family:"ＭＳ Ｐゴシック","ＭＳ ゴシック",Lucida Sans,Verdana,sans-serif;
  font-size:medium !important; 
}
body{
  font-family:"ＭＳ Ｐゴシック","ＭＳ ゴシック",Lucida Sans,Verdana,sans-serif;
  font-size:small;
}
a[href*="sage"]{
  color: green !important;

}
dl{
  margin-right: 0px !important;  
}
form + br,
form + br + br,  
img[src*="img.2ch.net/"],
img[src*="img.2ch.net/"] + br
  {
  display: none !important;
}
dd a img:hover{
  height: 400px !important;
}
dd a img:active{
  height: auto !important;
}
}
