css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vdisk.weibo.com") {
  
  
  
  
[id*="share"] .sort_name_detail a[href*="http://vdisk.weibo.com/s/"] ,
.find_file_main .sort_name_detail a[href*="http://vdisk.weibo.com/s/"] {
  font-size: 0px;
}
[id*="share"] .sort_name_detail a[href*="http://vdisk.weibo.com/s/"]::after,
.find_file_main .sort_name_detail a[href*="http://vdisk.weibo.com/s/"]::after {
  content: attr(title);
  font-size: 12px;
  line-height: 32px;
  z-index: 22;
  position: relative;
}
  

  
.vd_hotshare .sort_name_detail a:hover {
    color: #3185C7;
}
[id*="share"]  .folder_sort_main .sort_name_intro ,
.find_file_main .folder_sort_main .sort_name_intro ,
.folder_sort_main .vd_profile .sort_name_intro a,
.vd_profile .sort_name_detail a,
.folder_sort_main .sort_name_intro {
    width: auto !important;
}
[id*="share"] th.sort_set_m ,
.find_file_main th.sort_set_m ,
.vd_hotshare .v_sort_body .sort_set_m,
.vd_profile .v_sort_body .sort_set_m,
.vd_profile .sort_btn_m
  {
  z-index: 33;
  position: relative;
}
[id*="share"]  tr.filelist:hover .sort_size_m,
[id*="share"]  tr.filelist:hover  td.sort_downnum_m ,
.find_file_main tr.filelist:hover .sort_size_m,
.find_file_main tr.filelist:hover  td.sort_downnum_m 
 {
  z-index: 33 !important;
  position: relative;
  background: #F7F7F7;
  color: #444;
}
[id*="share"]  td.sort_size_m,
[id*="share"]  td.sort_downnum_m,
.find_file_main td.sort_size_m,
.find_file_main td.sort_downnum_m,
.vd_profile .v_sort_body .sort_set_m{
  line-height: 32px !important;
}
[id*="share"] .jspVerticalBar,
.find_file_main .jspVerticalBar {
  z-index: 44;
}
}
