css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url("about:home") {

body {background: #252525!important;}
          
.searchSuggestionTable {background-color: #333!important;color: #8995A1!important;}
    
input {box-shadow: none !important;color: #8995A1 !important;}
    
#brandLogo,#defaultSnippet1, #defaultSnippet2 {display: none !important;}
      
#launcher {border-top: none !important;box-shadow: none !important;} 
    
#searchText{background-color: #333 !important;}
    
#searchText:focus + #searchSubmit,
#searchText + #searchSubmit:hover,
#searchText[autofocus] + #searchSubmit,
#searchText:focus,
#searchText[autofocus] {border-color: #253424 !important;color: #8995A1!important;}
    
#searchSubmit {background: #253424!important;}
  
#searchText, #snippets, .launchButton {color: #8995A1 !important;}
    
.launchButton {background: #253424 !important;}
.launchButton:hover {background-color: #333 !important;}

}
