css:
@-moz-document domain("postd.cc") {
#masthead,
.navigation-wrap, .block-subscribe, .block-social,
.block-text-licence,
.block-entry-tags, #post-hourly, #secondary, .backstretch
{ display: none !important; }

#primary {
  float: none;
  width: auto;
}
#content {
  max-width: none;
}

body {
  font-feature-settings: "palt";
}

p {
  margin-bottom: 1em;
}

table td {
  line-height: 1.3;
}
tr.headers td + td {
  background-color: #ddd;
}
tr td + td {
  background-color: #f5f5f5;
}
}
