css:
@-moz-document domain("gamefaqs.com") {
  .msg_infobox {
    float: left;
    width: 15%;
    display: block;
    min-height: 100px;
  }
  .msg_body {
    float: right;
    width: 85%;
    margin: 0px !important;
    padding: 10px !important;
    display: block;
  }
  .msg_below {
    float: right;
    width: 85%;
    display: block;
  }
  .post_time, .name, .user, .tag {
    float: left;
    clear: both;

  }
}
