css:
@-moz-document url-prefix(https://www.glassdoor.) {
i:before {
    display: block;
    position: absolute;
    top: -6px;
    left: -6px;
    width: 20px;
    padding: 1px 2px;
    border-radius: 3px;
    font-size: 10px;
    color: white;
    text-align: center;
}

i.green,
i.yellow,
i.red {
    position: relative;
}

i.green:before {
    content: 'cool';
    background: #7cb228;
}

i.yellow:before {
    content: 'ok';
    background: #fde700;
    color: black;
}

i.red:before {
    content: 'bad';
    background: #b24127;
}
}
