css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.youtube.com") {

#movie_player {
  width: 615px !important;
  height: 460px !important;
}

div#watch-vid-title {
  width: 580px !important;
  margin: 0 auto !important;
}

/* center title & video */

div#watch-this-vid {
  color: #fff !important;
  margin: 0 auto !important;
  float: none !important;
  width: 615px !important;
}

/* hide crap */
div.watch-wrapper, div.watch-tab-contents, div#old-footer, 
div#old-masthead, div#watch-other-vids, div#copyright,
div#watch-comments-stats {
  display: none !important;
}

div#watch-main-area {
  border: 0 !important;
}

/* set up some good colors for watching videos on */
span, a, a:link, a:visited, h1 {
  color: #fff !important;
  font-weight: normal !important;
}

body {
  font-size: 40% !important;
  background-color: #000 !important;
}


}
