css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("http://na\\.finalfantasyxiv\\.com/lodestone/playguide/db/.*") {

  /* fixed top bar */
  header { display: none !important; }
  a#pageTop { display: none !important; }

  /* lodestone social integration near article */
  div.item_detail > div.right_counter { display: none !important; }
  ul#sns_diary { display: none !important; }

  /* Lodestone tooltip code */
  div.embed_code_wrap { display: none !important; }

  /* Order of comments */
  select[name=order] { display: none !important; }

  /* Report comment */
  a.bt_report { display: none !important; }

  /* login */
  div.bg_black { display: none !important; }

  /* banners and disclaimer at bottom */
  footer { display: none !important; }
}
